import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DrinkEndpointService } from 'src/app/shared/drink-endpoint.service';
import { DrinkModel } from 'src/app/shared/drink.model';

@Component({
  selector: 'app-drink-details',
  templateUrl: './drink-details.component.html',
  styleUrls: ['./drink-details.component.css']
})
export class DrinkDetailsComponent {

  drink!: DrinkModel;

constructor(private drinkEndpoint: DrinkEndpointService, private route: ActivatedRoute){

  this.drinkEndpoint.getById(this.route.snapshot.params['id']).subscribe( data =>{
    this.drink = data as DrinkModel
  });
}

}
<div class="d-flex h-100 justify-content-center align-items-center mainBody">
    <div class="d-flex h-100 justify-content-center align-items-center">
        <div>
            <img [src]="'./../../assets/'+ drink.drinkPhotoId" class="pinkborda mx-1" style="max-height: 420px;" alt="...">
        </div>
    </div>


    <div class="d-inline-block mx-1">
        <p class="display-5 fw-bolder">drink.name</p>
        <p class="lead fw-bolder">Alkohol dominujący: drink.mainAlcohol.name</p>
        <p class="lead fw-bolder">Poziom trudności: drink.difficulty.name</p>
        <p class="lead fw-bolder">Składniki:</p>
        <ul>
            <li class="lead fw-bold" *ngFor="let ingredient of drink.ingredients">{{ingredient.name}}: {{ingredient.quantity}} {{ingredient.unit.name}}</li>
        </ul>
        <p class="lead fw-bolder">Opis przygotowania: </p>
        <p class="lead fw-bolder">drink.description</p>
        <a class="btn btn-lg btn-warning fw-bolder my-2" onclick="history.back()">Powrót</a>
    </div>

</div>
