import { Component } from '@angular/core';

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent {

}
<div class="d-block mainBody justify-content-center p-4">
        <p>Enter your Email. If it's in the base, we will send you code to reset your password.</p>
        <div class="mb-3">
            <label for="authEmail" class="form-label">Adres E-mail</label>
            <input type="email" class="form-control" id="authEmail" placeholder="email@example.com" style="max-width: 250px">
        </div>
</div>
